<?php include 'config.php'; $query = "SELECT vote_choice, COUNT(*) AS vote_count FROM Votes GROUP BY vote_choice"; $result = $conn->query($query); echo "<h2>üó≥Ô∏è Voting Results</h2>"; if ($result->num_rows > 0) { echo "<table border='1' cellpadding='10'>"; echo "<tr><th>Candidate</th><th>Votes</th></tr>"; while ($row = $result->fetch_assoc()) { echo "<tr><td>" . htmlspecialchars($row['vote_choice']) . "</td><td>" . $row['vote_count'] . "</td></tr>"; } echo "</table>"; } else { echo "No votes have been cast yet."; } $conn->close(); ?>